defined( '_JEXEC' ) or die( 'Restricted access' );

jimport( 'joomla.filesystem.folder' );

class com_phocagalleryInstallerScript
{
	protected $extension 	= 'com_phocagallery';
	protected $updatetext 	= 'COM_PHOCAGALLERY_UPDATE_TEXT';
	protected $installtext 	= 'COM_PHOCAGALLERY_INSTALL_TEXT';
	protected $versiontext	= 'COM_PHOCAGALLERY_VERSION';
	protected $extensiontext= 'COM_PHOCAGALLERY';
	protected $configuretext= 'COM_PHOCAGALLERY_CONFIGURE';
	
	
		function createFolders() {
	
		$folder[0][0]	=	'images/phocagallery/' ;
		$folder[0][1]	= 	JPATH_ROOT . '/'.  $folder[0][0];
		$folder[1][0]	=	'images/phocagallery/avatars/';
		$folder[1][1]	= 	JPATH_ROOT . '/' .  $folder[1][0];
		
		$msg = '';
		foreach ($folder as $k => $v) {
   
			if (!JFolder::exists( $v[1])) {
	
				if (JFolder::create( $v[1], 0755 )) {
	 
	 
					$data = "<html>\n<body bgcolor=\"#FFFFFF\">\n</body>\n</html>";
					JFile::write($v[1].'/'."index.html", $data);
					$msg .= '<div><b><span style="color:#009933">Folder</span> ' . $v[0] 
						 .' <span style="color:#009933">created!</span></b></div>';
				  
	   
				} else {
	 
					$msg .= '<div><b><span style="color:#CC0033">Folder</span> ' . $v[0]
						 .' <span style="color:#CC0033">creation failed!</span></b> Please create it manually.</div>';
				  
				}
	
			} else {
				// Folder exists
				$msg .= '<div><b><span style="color:#009933">Folder</span> ' . $v[0] 
					 .' <span style="color:#009933">exists!</span></b></div>';
				 
			}
		}
		return $msg;
	}
	
	function install($parent) {
		$this->loadLanguage($parent);
		$msg = $this->createFolders();
		JFactory::getApplication()->enqueueMessage($msg, 'message');
		return true;
	}
	function uninstall($parent) {
		//$this->loadLanguage($parent);
		return true;
	}

	function update($parent) {
		//$this->loadLanguage($parent);
		$msg = $this->createFolders();
		JFactory::getApplication()->enqueueMessage($msg, 'message');
		return true;
	}
	
	public function loadLanguage($parent) {
		$extension = $this->extension;
		$lang = JFactory::getLanguage();
		$path = $parent->getParent()->getPath('source');
		$lang->load($this->extension, $path, 'en-GB', true);
		$lang->load($this->extension, $path, $lang->getDefault(), true);
		$lang->load($this->extension, $path, null, true);
		$lang->load($this->extension . '.sys', $path, 'en-GB', true);
		$lang->load($this->extension . '.sys', $path, $lang->getDefault(), true);
		$lang->load($this->extension . '.sys', $path, null, true);
		return true;
	}

	function preflight($type, $parent) {
		$this->loadLanguage($parent);
		return true;
	}

	function postflight($type, $parent)  {
		$this->loadLanguage($parent);
		
		if ($type == 'update' || $type == 'install') {
			
			if ($type == 'update') {
				$status =  JText::_($this->updatetext);
	
			} else {
				$status =  JText::_($this->installtext);
			}
			$version 	= JText::_($this->versiontext). ': ' . $parent->getManifest()->version;
			$link 		= 'index.php?option='.$this->extension;
			$component	= JText::_($this->extensiontext);
			$configure	= JText::_($this->configuretext);
			
			$o = '';
			$o .= $this->getStyle();
			$o .= '<div class="g5i">';
			$o .= ' <h1>';
			$o .= '  <span class="g5-title">'.$status.'</span>';
			$o .= '  <span class="g5-info">'.$version.'</span>';
			$o .= ' </h1>';
			$o .= ' ';
			$o .= ' <div class="g5-actions">';
			$o .= '  <a href="'.$link.'" class="g5-button">'.$configure.' '.$component.'<span class="g5-icon icon-chevron-right"></span></a>';
			$o .= ' </div>';
			$o .= ' <div class="g5-phoca">';
			$o .=    '<a href="https://www.phoca.cz" target="_blank"><span>Phoca</span></a>';
			$o .= ' </div>';
			$o .= '</div>';
			
			echo $o;
		}
		
		return true;
	}
	
	
	function getStyle() {
		
		return "<style>
.g5i h1 {
	color: white;
	background: rgba(0, 0, 0, 0.3);
	padding: 1rem;
	text-align: center;
	left: 50%;
	position: relative;
	/*margin: 1rem -0.5rem 0;*/
	margin: 1rem 0 0 0;
	transform: translateX(-50%);
	line-height: 1.5;
	border-top: 1px solid rgba(0, 0, 0, 0.1);
}

.g5i p {
	font-size: 1rem;
	color: white;
	margin: 1rem 10%;
	text-shadow: 0 0 9px black;
	text-align: center;
	background: rgba(0, 0, 0, 0.1);
	padding: 0.5rem;
	border-radius: 3px;
	line-height: 1.5;
}

.g5i .g5-info {
	display: block;
	font-size: 1rem;
	font-weight: normal;
}

.g5i .g5-actions {
	text-align: center;
	border-top: 1px solid rgba(255, 255, 255, 0.1);
	padding: 1rem;
}

.g5i .g5-button {
	display: inline-block;
	font-size: 1rem;
	color: white;
	border: 2px solid rgba(255, 255, 255, 0.8);
	border-radius: 3px;
	padding: 0.5rem 1rem;
	background: rgba(82, 195, 255, 0.1);
	vertical-align: middle;
	transition: background .2s;
	text-decoration: none;
}

.g5i .g5-button:hover {
	background-color: rgba(255, 255, 255, 0.2);
	transition: background .2s;
}

.g5i .g5-icon {
	line-height: 11px;
	vertical-align: middle;
}

.g5i .g5-phoca {
        background: url('data:image/png;base64,');
	width: 130px;
	height: 31px;
	background-repeat: no-repeat;
	position: absolute;
	bottom: 5px;
	right: 5px;
	background-size: contain;
}

.g5i .g5-phoca a {
	display: block;
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	font-size: 0;
}

/* container */
.g5i {
	position: relative;
	margin: 15px 15px 15px -15px;
	background: url('data:image/jpeg;base64,');
	padding: 1rem;
	background-repeat: no-repeat;
	background-size: cover;
	border-radius: 6px;
}
</style>";
	}

}